CREATE EXTENSION IF NOT EXISTS "uuid-ossp";
CREATE TYPE "public"."tb_transaction_code_enum" AS ENUM('brl', 'btc');
CREATE TYPE "public"."tb_transaction_transaction_type_enum" AS ENUM('credit', 'debit');
CREATE TABLE "tb_transaction" ("id" uuid NOT NULL DEFAULT uuid_generate_v4(), "created_at" TIMESTAMP NOT NULL DEFAULT now(), "updated_at" TIMESTAMP NOT NULL DEFAULT now(), "amount" integer NOT NULL, "code" "public"."tb_transaction_code_enum" NOT NULL, "transaction_type" "public"."tb_transaction_transaction_type_enum" NOT NULL, "user_id" uuid, CONSTRAINT "PK_60ebc65df88c25409fc36c958f9" PRIMARY KEY ("id"));
CREATE TABLE "tb_user" ("id" uuid NOT NULL DEFAULT uuid_generate_v4(), "created_at" TIMESTAMP NOT NULL DEFAULT now(), "updated_at" TIMESTAMP NOT NULL DEFAULT now(), "name" character varying(150) NOT NULL, "email" character varying(150) NOT NULL, "password" character varying(64) NOT NULL, CONSTRAINT "UQ_ebe445a8233800b2f59004d8ddc" UNIQUE ("email"), CONSTRAINT "PK_1943338f8f00e074a3c5bb48d5e" PRIMARY KEY ("id"));
ALTER TABLE "tb_transaction" ADD CONSTRAINT "FK_26ba469200d5ac7382c6350b8ee" FOREIGN KEY ("user_id") REFERENCES "tb_user"("id") ON DELETE NO ACTION ON UPDATE NO ACTION;
ALTER TABLE "tb_transaction" DROP COLUMN "amount";
ALTER TABLE "tb_transaction" ADD "amount" numeric(8,2) NOT NULL;
ALTER TABLE "tb_transaction" ALTER COLUMN "amount" TYPE numeric(27,18);
ALTER TABLE "tb_transaction" ADD "quotation" numeric(27,18) NOT NULL DEFAULT '1';
ALTER TABLE "tb_transaction" ADD "is_purchase" boolean NOT NULL DEFAULT false;
ALTER TABLE "tb_transaction" RENAME COLUMN "is_purchase" TO "is_negotiation";
CREATE TABLE "tb_quotation_history" ("id" uuid NOT NULL DEFAULT uuid_generate_v4(), "created_at" TIMESTAMP NOT NULL DEFAULT now(), "updated_at" TIMESTAMP NOT NULL DEFAULT now(), "buy_quotation" numeric(27,18) NOT NULL, "sell_quotation" numeric(27,18) NOT NULL, CONSTRAINT "PK_514057dd6bc1ff8205057e535a3" PRIMARY KEY ("id"));